            }
        }
        else
            show_error('The software_version you are trying to edit does not exist.');
    }

    /*
     * Deleting software_version
     */
    function remove($id)
    {
        $software_version = $this->Software_version_model->get_software_version($id);

        // check if the software_version exists before trying to delete it
        if(isset($software_version['id']))
        {
            $this->Software_version_model->delete_software_version($id);
            redirect('software_version/index');
